    #[DataProvider('additionProviderString')]
    public function testVariant1($expected, ...$values)
    {
        $this->assertSame($expected, BigDecimalAddHelper::variant1(...$values));
    }

    #[DataProvider('additionExceptionsProvider')]
    public function testExceptionsVariant1(...$values)
    {
        $this->expectException(ValueError::class);
        $_ = BigDecimalAddHelper::variant1(...$values);
    }

